mapper.select:
select board_like_no
from board_like
where user_no = #{uNo}
and board_no = #{bNo}

mapper.update:
update board_like set status = 'Y', like_date = 'now'
where user_no = #{uNo}
and board_no = #{bNo}

update board_like set status = 'N', like_date = 'now'
where user_no = #{uNo}
and board_no = #{bNo}
